  {
    id: 'e1',
    title: 'Toilet Paper',
    amount: 94.12,
    date: new Date(2020, 7, 14),
  },
  {
    id: 'e2',
    title: 'New TV',
    amount: 799.49,
    date: new Date(2021, 2, 12)
  },
  {
    id: 'e3',
    title: 'Car Insurance',
    amount: 294.67,
    date: new Date(2021, 2, 28),
  },
  {
    id: 'e4',
    title: 'New Desk (Wooden)',
    amount: 450,
    date: new Date(2021, 5, 12),
  },
]

// modern JS Function Syntax
const App = () => {
  const [expenses, newExpenses] = useState(dummyExpenses);

  const getDataFromNewExpenses = (expense) => {
    expense.submitedDate = new Date(expense.submitedDate)

    // console.log('in App.js')
    // console.log(expense)
    // console.log('date\n')
    newExpenses((prevExpenses) => {
      return [expense, ...prevExpenses]
    })

    // newExpenses([
    //   ...expenses, expense
    // ])

    console.log('in App.js')
    console.log(newExpenses)
    // console.log(expense)

    // expense.submitedDate = new Date(expense.submitedDate)
    // console.log(expense.submitedDate)


    // expense.submitedDate = new Date(expense.submitedDate)
    // console.log(expense.submitedDate.toLocaleString('en-US', { month: 'long' }))
    // console.log(expense.submitedDate.toLocaleString('en-US', { day: '2-digit' }))
    // console.log(expense.submitedDate.getFullYear())
  }

  return (
    <div>
      {/* <ExpenseItem title={expenses[0].title} amount={expenses[0].amount} date={expenses[0].date.toString().slice(0, 15)}></ExpenseItem> */}
      {/* <div>{expenses.map((expense) => (
        <ExpenseItem title={expense.title} amount={expense.amount} date={expense.date}></ExpenseItem>
      ))}</div> */}
      <NewExpenses saveExpense={getDataFromNewExpenses} />
      <Expenses expenses={expenses}></Expenses>
    </div>
  );
}

export default App;
