import { useState } from 'react';
import Card from '../General/CardComponent';
import ExpenseItem from './ExpenseItem';
import ExpenseFilter from './ExpenseFilter'

import '../../styles/Expenses.css';

function Expenses(props) {

    const [filterYear, setFilterYear] = useState('all')

    function getFilterYearHandler(year) {
        // console.log('in expense file')
        // console.log(year)
        setFilterYear(year)

    }

    function showExpenseItem() {
        const expenseItem = props.expenses
        const showExpense = filterYear

        const runFilter = expenseItem.filter(expense => {
            return expense.date.getFullYear().toString() === filterYear
        }).map((expense) => (
            <ExpenseItem key={expense.id} title={expense.title} amount={expense.amount} date={expense.date}></ExpenseItem>
        ))

        if (showExpense === 'all') {
            return expenseItem.map((expense) => (
                <ExpenseItem key={expense.id} title={expense.title} amount={expense.amount} date={expense.date}></ExpenseItem>
            ))
        } else if (showExpense === '2020' && expenseItem.length > 0) {
            return runFilter
        } else if (showExpense === '2021' && expenseItem.length > 0) {
            return runFilter
        }
        else if (showExpense === '2022' && expenseItem.length > 0) {
            return runFilter
        }
        else if (showExpense === '2023' && expenseItem.length > 0) {
            return runFilter
        } else {
            return <h2>Data is Empty</h2>
        }

    }

    const filteredExpenses = props.expenses.filter(expense => {
        return expense.date.getFullYear().toString() === filterYear
    })

    return (
        <div>
            <Card className="expenses">
                <ExpenseFilter selectedYear={filterYear} setYear={getFilterYearHandler} />
                <p>{filterYear}</p>
                {showExpenseItem()}
                {/* {props.expenses.map((expense) => (
                    <ExpenseItem key={expense.id} title={expense.title} amount={expense.amount} date={expense.date}></ExpenseItem>
                ))} */}
            </Card>
        </div>
    );
}

export default Expenses;
