import ExpenseForm from './ExpenseForm';
import '../../styles/NewExpenses.css';
import { useState } from 'react';

const NewExpenses = (props) => {

    let [idExpense, setIdExpense] = useState('5')
    const getUserInputHandler = (newExpenseData) => {
        function generateID() {
            for (let id = idExpense; idExpense > 5; id++) {
                setIdExpense(id)
            }
        }

        const expenseData = {
            // id: Math.random().toString().slice(3, 5),
            id: 'e' + generateID(idExpense),
            ...newExpenseData
        }
        // send data expenses to App.js
        props.saveExpense(expenseData)
        // console.log(expenseData)
    }

    return (
        <div className='new-expense'>
            <ExpenseForm saveDataInput={getUserInputHandler} />
        </div>
    );
};

export default NewExpenses;
