import Chart from "../Chart/Chart"


const ExpensesChart = (props) => {
    const chartDatapoints = [
        { label: 'Jan', value: 0 },
        { label: 'Feb', value: 0 },
        { label: 'Mar', value: 0 },
        { label: 'Apr', value: 0 },
        { label: 'May', value: 0 },
        { label: 'Jun', value: 0 },
        { label: 'Jul', value: 0 },
        { label: 'Aug', value: 0 },
        { label: 'Sept', value: 0 },
        { label: 'oct', value: 0 },
        { label: 'Nov', value: 0 },
        { label: 'Dec', value: 0 },
    ]

    for (const expenseAllData in props.expenses) {
        const getMonth = expenseAllData.date.getMonth()
        chartDatapoints[getMonth].value += expenseAllData.amount
        console.log('in ExpensesChart')
        console.log(getMonth)
    }

    // function getMonthh() {
