import Chart from "../Chart/Chart"


const ExpensesChart = (props) => {
    const chartDatapoints = [
        { label: 'Jan', value: 0 },
        { label: 'Feb', value: 0 },
        { label: 'Mar', value: 0 },
        { label: 'Apr', value: 0 },
        { label: 'May', value: 0 },
        { label: 'Jun', value: 0 },
        { label: 'Jul', value: 0 },
        { label: 'Aug', value: 0 },
        { label: 'Sept', value: 0 },
        { label: 'oct', value: 0 },
        { label: 'Nov', value: 0 },
        { label: 'Dec', value: 0 },
    ]

    const xx = props.showChart.map((expense) => expense.date)
    console.log('test')
    // console.log(xx[0])
    // console.log(xx[0].getMonth())
    let newArr = []
    let iterator = xx.values();

    // Here all the elements of the array is being printed.
    for (let elements of iterator) {
        newArr.push(elements.getMonth())
        // console.log(elements.getMonth());
    }
    console.log(newArr)

    const datasChart = props.showChart.map((expense) => expense.date)

    let newTempArr = []
    let getValue = datasChart.values()

    for (let expenseDate of getValue) {
        newTempArr.push(expenseDate.getMonth())
    }

    console.log(newTempArr)


    // console.log('in Expense Chart')


    // for (const expenseAllData in props.showChart) {
    //     const getMonth = expenseAllData.date.getMonth()
    //     chartDatapoints[getMonth].value += expenseAllData.amount
    // }


    return <Chart dataPoint={chartDatapoints}></Chart>
    // return <p>{getMonthh()}</p>
}

export default ExpensesChart
