    // }
    // console.log(newTempYear)

    // const getAmount = props.showChart.map((expense) => expense.amount)
    // let readValue = getAmount.values()
    // let newTempAmount = []
    // for (let expenseAmount of readValue) {
    //     newTempAmount.push(expenseAmount)
    // }

    // console.log(newTempAmount)

    for (const expenseAllData of props.showChart) {
        const getMonth = expenseAllData.date.getMonth()
        chartDatapoints[getMonth].value += expenseAllData.amount
    }




    // console.log('in Expense Chart')


    // for (const expenseAllData in props.showChart) {
    //     const getMonth = expenseAllData.date.getMonth()
    //     chartDatapoints[getMonth].value += expenseAllData.amount
    // }


    return <Chart dataPoint={chartDatapoints}></Chart>
    // return <p>{getMonthh()}</p>
}

export default ExpensesChart
