import { useState } from 'react';
import '../../styles/ExpenseForm.css'

const ExpenseForm = (props) => {

    // const [enteredTitle, setEnteredTitle] = useState('');
    // const [enteredAmount, setEnteredAmount] = useState('');
    // const [enteredDate, setEnteredDate] = useState('');

    const [userInput, newUserInput] = useState({
        submitedDate: '',
        submitedTitle: '',
        submitedAmount: '',
    })

    //safer way update with previous state
    const getInputDateHandler = (event) => {
        // setEnteredDate(event.target.value);
        newUserInput((prevState) => {
            return {
                ...prevState,
                submitedDate: event.target.value
            }
        })
    }
    const getInputTitleHandler = (event) => {
        // console.log(event.target)
        // setEnteredTitle(event.target.value);
        newUserInput({
            ...userInput,
            submitedTitle: event.target.value
        })
    }
    const getInputAmountHandler = (event) => {
        // setEnteredAmount(event.target.value);
        newUserInput({
            ...userInput,
            submitedAmount: event.target.value
        })
    }

    function submitHandler(event) {
        // prevent reload page for request data to development server 
        // default javascript
        event.preventDefault();
        // console.log(userInput)
        // userInput.submitedDate = new Date(userInput.submitedDate)
        // const expenseData = {
        //     title: enteredTitle,
        //     amount: enteredAmount,
        //     date: new Date(enteredDate)
        // };
        console.log('in ExpenseForm.js')
        // console.log(expenseData)
        // props.saveDataInput(expenseData)
        // setEnteredTitle('');
        // setEnteredAmount('');
        // setEnteredDate('');
        // after get data, input will be reset to default
        // newUserInput({
        //     submitedDate: '',
        //     submitedTitle: '',
        //     submitedAmount: '',
        // })
    }
    return (
        <form onSubmit={submitHandler}>
            <div className='new-expense__controls'>
                <div className='new-expense__control'>
                    <label>Date</label>
                    <input type='date' min='2019-01-01' max='2022-12-31' value={userInput.submitedDate} onChange={getInputDateHandler} />
                </div>
                <div className='new-expense__control'>
                    <label>Title</label>
                    <input type='text' value={userInput.submitedTitle} onChange={getInputTitleHandler} />
                </div>
                <div className='new-expense__control'>
                    <label>Amount</label>
                    <input type='number' min='0.01' step='0.01' value={userInput.submitedAmount} onChange={getInputAmountHandler} />
                </div>
            </div>
            <div className='new_expense__actions'>
                <button type='submit'>Add Expense</button>
            </div>
        </form>
    );
};

export default ExpenseForm;
