import Chart from "../Chart/Chart"


const ExpensesChart = (props) => {
    const chartDatapoints = [
        { label: 'Jan', value: 0 },
        { label: 'Feb', value: 0 },
        { label: 'Mar', value: 0 },
        { label: 'Apr', value: 0 },
        { label: 'May', value: 0 },
        { label: 'Jun', value: 0 },
        { label: 'Jul', value: 0 },
        { label: 'Aug', value: 0 },
        { label: 'Sept', value: 0 },
        { label: 'oct', value: 0 },
        { label: 'Nov', value: 0 },
        { label: 'Dec', value: 0 },
    ]

    const getDate = props.showChart.map((expense) => expense.date)
    const getAmount = props.showChart.map((expense) => expense.amount)
    console.log(getDate)
    let newTempYear = []
    let newTempAmount = []
    let readValue = getAmount.values()
    let getValue = getDate.values()
    for (let expenseDate of getValue) {
        newTempYear.push(expenseDate.getMonth())
    }
    for (let expenseAmount of readValue) {
        newTempAmount.push(expenseAmount.amount)
    }
    console.log(newTempYear)
    console.log(newTempAmount)

    // for (const expenseAllData in datasChart) {
    //     chartDatapoints[newTempYear].value += expenseAllData.amount
    // }




    // console.log('in Expense Chart')


    // for (const expenseAllData in props.showChart) {
    //     const getMonth = expenseAllData.date.getMonth()
    //     chartDatapoints[getMonth].value += expenseAllData.amount
    // }


    return <Chart dataPoint={chartDatapoints}></Chart>
    // return <p>{getMonthh()}</p>
}

export default ExpensesChart
