import { useState } from 'react';

import '../../styles/InvestInput.css'
const InvestInput = (props) => {

    const [userInput, setUserInput] = useState({
        current_saving: '',
        year_saving: '',
        expected_return: '',
        invest_duration: '',
        data_calculated: false
    })

    function getCurrentSavingHandler(event) {

        setUserInput((prevState) => {
            return { ...prevState, current_saving: event.target.value }
        })
    }
    function getYearlySavingHandler(event) {
        setUserInput((prevState) => {
            return { ...prevState, year_saving: event.target.value }
        })
    }
    function getReturnHandler(event) {

        setUserInput((prevState) => {
            return { ...prevState, expected_return: event.target.value }
        })
    }
    function getDurationHandler(event) {
        setUserInput((prevState) => {
            return { ...prevState, invest_duration: event.target.value }
        })
    }

    function submitHandler(event) {
        event.preventDefault();
        // console.log(userInput)
        const allData = {
            currentSavings: +userInput.current_saving,
            yearlyContribution: +userInput.year_saving,
            expectedReturn: +userInput.expected_return,
            duration: +userInput.invest_duration,
            dataCalculated: userInput.data_calculated
        }
        props.getInputData(allData)
    }
    return (
        <form className="form" onSubmit={submitHandler}>
            <div className="input-group">
                <p>
                    <label htmlFor="current-savings">Current Savings ($)</label>
                    <input type="number" id="current-savings" onChange={getCurrentSavingHandler} />
                </p>
                <p>
                    <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
                    <input type="number" id="yearly-contribution" onChange={getYearlySavingHandler} />
                </p>
            </div>
            <div className="input-group">
                <p>
                    <label htmlFor="expected-return">
                        Expected Interest (%, per year)
                    </label>
                    <input type="number" id="expected-return" onChange={getReturnHandler} />
                </p>
                <p>
                    <label htmlFor="duration">Investment Duration (years)</label>
                    <input type="number" id="duration" onChange={getDurationHandler} />
                </p>
            </div>
            <p className="actions">
                <button type="reset" className="buttonAlt">
                    Reset
                </button>
                <button type="submit" className="button">
                    Calculate
                </button>
            </p>
        </form>
    )
}

export default InvestInput;
