import { useState } from 'react';

import '../../styles/InvestInput.css'
import FirstFormGroup from './InvestmentFormGroup/FirstFormGroup';
import SecondFormGroup from './InvestmentFormGroup/SecondFormGroup';
const InvestInput = (props) => {


    const [userInput, setUserInput] = useState({
        current_saving: '',
        year_saving: '',
        expected_return: '',
        invest_duration: ''
    })

    const getFirstFormData = (dataInput) => {
        const allData = {
            current_saving: dataInput.current_saving,
            year_saving: dataInput.year_saving
        }

        // setUserInput((prevInput) => {
        //     return {
        //         ...prevInput, current_saving: dataInput.current_saving,

        //     }
        // })

        setUserInput((prevInput) => {
            return { ...prevInput, ...allData }
        })
    }

    const getSecondFormData = (dataInput) => {
        const allData = {
            expected_return: dataInput.expected_return,
            invest_duration: dataInput.invest_duration
        }
    }


    return (
        <form className="form">
            {/* <div className="input-group">
                <p>
                    <label htmlFor="current-savings">Current Savings ($)</label>
                    <input type="number" id="current-savings" onChange={getCurrentSavingHandler} />
                </p>
                <p>
                    <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
                    <input type="number" id="yearly-contribution" />
                </p>
            </div> */}
            <FirstFormGroup getData={getFirstFormData} />
            <SecondFormGroup getData={getSecondFormData} />
            {/* <div className="input-group">
                <p>
                    <label htmlFor="expected-return">
                        Expected Interest (%, per year)
                    </label>
                    <input type="number" id="expected-return" />
                </p>
                <p>
                    <label htmlFor="duration">Investment Duration (years)</label>
                    <input type="number" id="duration" />
                </p>
            </div> */}
            <p className="actions">
                <button type="reset" className="buttonAlt">
                    Reset
                </button>
                <button type="submit" className="button">
                    Calculate
                </button>
            </p>
        </form>
    )
}

export default InvestInput;
