import { useState } from "react";
import ResultItem from "./ResultItem";

const ResultList = (props) => {

    const investSubmited = props.setDataInvest
    console.log(investSubmited)

    const [calculatedData, SetCalculatedData] = useState({
        year: '',
        yearlyInterest: '',
        savingsEndOfYear: '',
        yearlyContribution: ''
    })

    function calculateHandler() {
        const yearlyData = [];

        let currentSavings = investSubmited.currentSavings// feel free to change the shape of this input object!
        const yearlyContribution = investSubmited.yearlyContribution // as mentioned: feel free to change the shape...
        const expectedReturn = investSubmited.expectedReturn / 100;
        const duration = investSubmited.duration;

        for (let i = 0; i < duration; i++) {
            const yearlyInterest = currentSavings * expectedReturn;
            currentSavings += yearlyInterest + yearlyContribution;
            yearlyData.push({
                // feel free to change the shape of the data pushed to the array!
                year: i + 1,
                yearlyInterest: yearlyInterest,
                savingsEndOfYear: currentSavings,
                yearlyContribution: yearlyContribution,
            });
        }
        SetCalculatedData(yearlyData)
    }

    console.log(calculatedData)

    return (
        <div>
            <ResultItem showResultData={calculateHandler()} />
        </div>
    )
}

export default ResultList;
