const ResultContent = (props) => {

    const tableContent = props.showContent
    const currentSaving = props.setSaving
    // const formatCurrency = new Intl.NumberFormat('en-US', {
    //     style: 'currency',
    //     currency: 'USD',
    //     minimumFractionDigits: 2,
    //     maximumFractionDigits: 2,
    // });

    return (
        <table className="result">
            <tbody>
                {tableContent.map((yearData) => (
                    <tr key={yearData.year}>
                        <td>{yearData.year}</td>
                        <td>{yearData.savingsEndOfYear}</td>
                        <td>{yearData.yearlyInterest}</td>
                        <td>{yearData.savingsEndOfYear - currentSaving - (yearData.yearlyContribution * yearData.year)}</td>
                        <td>{currentSaving + (yearData.yearlyContribution * yearData.year)}</td>
                    </tr>
                ))}
            </tbody>
        </table>
    )
}

export default ResultContent;
