import { useState } from "react";
import ResultItem from "./ResultItem";

const ResultList = (props) => {

    const investSubmited = props.setDataInvest
    console.log(investSubmited)

    if (investSubmited.length === 0) {
        console.log('empty')
    } else {
        console.log('succeed')
        console.log(investSubmited.length)
    }

    const [calculatedData, SetCalculatedData] = useState(null)

    function calculateHandler() {
        SetCalculatedData(investSubmited)
    }

    const yearlyData = [];

    let currentSavings = calculatedData['current-savings']; // feel free to change the shape of this input object!
    const yearlyContribution = calculatedData['yearly-contribution']; // as mentioned: feel free to change the shape...
    const expectedReturn = calculatedData['expected-return'] / 100;
    const duration = calculatedData['invest-duration'];

    for (let i = 0; i < duration; i++) {
        const yearlyInterest = currentSavings * expectedReturn;
        currentSavings += yearlyInterest + yearlyContribution;
        yearlyData.push({
            // feel free to change the shape of the data pushed to the array!
            year: i + 1,
            yearlyInterest: yearlyInterest,
            savingsEndOfYear: currentSavings,
            yearlyContribution: yearlyContribution,
        });
    }

    // function calculateHandler() {
    //   
