    //     SetCalculatedData(yearlyData)
    // }


    return (
        <div>
            {/* <ResultItem getResultData={calculateHandler} showResultData={calculatedData} /> */}
            {/* <p>{calculatedData}</p> */}
            <ResultItem />
        </div>
    )
}

export default ResultList;
