import { useState } from 'react';

import '../../styles/InvestInput.css'

// const dummyUserInput = {
//     'current-savings': 5000,
//     'yearly-contribution': 1200,
//     'expected-return': 7,
//     'invest-duration': 3,
// }
const dummyUserInput = {
    'current-savings': '',
    'yearly-contribution': '',
    'expected-return': '',
    'invest-duration': '',
}

const InvestInput = (props) => {

    const [userInput, setUserInput] = useState(dummyUserInput)

    function getInputHandler(inputID, value) {
        // console.log(inputID, value)
        setUserInput((prevInput) => {
            return {
                ...prevInput,
                [inputID]: value
            }
        })
    }

    function submitHandler(event) {
        event.preventDefault();
        // console.log(userInput)
        props.getInputData(userInput)
    }

    function resetHandler() {
        setUserInput(dummyUserInput)
    }
    return (
        <form className="form" onSubmit={submitHandler}>
            <div className="input-group">
                <p>
                    <label htmlFor="current-savings">Current Savings ($)</label>
                    <input type="number" id="current-savings" onChange={(event) => { getInputHandler('current-savings', event.target.value) }} />
                </p>
                <p>
                    <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
                    <input type="number" id="yearly-contribution" onChange={(event) => { getInputHandler('yearly-contribution', event.target.value) }} />
                </p>
            </div>
            <div className="input-group">
                <p>
                    <label htmlFor="expected-return">
                        Expected Interest (%, per year)
                    </label>
                    <input type="number" id="expected-return" onChange={(event) => { getInputHandler('expected-return', event.target.value) }} />
                </p>
                <p>
                    <label htmlFor="duration">Investment Duration (years)</label>
                    <input type="number" id="duration" onChange={(event) => { getInputHandler('invest-duration', event.target.value) }} />
                </p>
            </div>
            <p className="actions">
                <button type="reset" className="buttonAlt" onClick={resetHandler}>
                    Reset
                </button>
                <button type="submit" className="button">
                    Calculate
                </button>
            </p>
        </form>
    )
}

export default InvestInput;
