import '../../styles/ResultTable.css'
import ResultContent from './ResultContent';

const ResultTable = (props) => {

    const tableContent = props.investData
    const yearlyData = []
    if (tableContent === null) {

    } else {

        let currentSavings = tableContent['current-savings'];
        const yearlyContribution = tableContent['yearly-contribution'];
        const expectedReturn = tableContent['expected-return'] / 100;
        const duration = tableContent['invest-duration'];

        for (let i = 0; i < duration; i++) {
            const yearlyInterest = currentSavings * expectedReturn;
            currentSavings += yearlyInterest + yearlyContribution;
            yearlyData.push({
                year: i + 1,
                yearlyInterest: yearlyInterest,
                savingsEndOfYear: currentSavings,
                yearlyContribution: yearlyContribution,
            });
        }

    }



    return (
        <table className="result">
            <thead>
                <tr>
                    <th>Year</th>
                    <th>Total Savings</th>
                    <th>Interest (Year)</th>
                    <th>Total Interest</th>
                    <th>Invested Capital</th>
                </tr>
            </thead>
            {/* <tbody>
                {tableContent.map((yearData) => (
                    <tr key={yearData.year}>
                        <td>{yearData.year}</td>
                        <td>{yearData.savingsEndOfYear}</td>
                        <td>{yearData.yearlyInterest}</td>
                        <td>{yearData.savingsEndOfYear - currentSaving - (yearData.yearlyContribution * yearData.year)}</td>
                        <td>{currentSaving + (yearData.yearlyContribution * yearData.year)}</td>
                    </tr>
                ))}
            </tbody> */}
            <ResultContent showContent={yearlyData} setSaving={tableContent['current-savings']} />
        </table>
    )
}

export default ResultTable;
