import Input from '../../general/Input';
import MealFormStyles from '../../styles/MealForm.module.css'

const MealForm = (props) => {
    const setInput = {
        id: 'amount' + props.id,
        type: 'number',
        min: 1,
        max: 5,
        step: 1,
        defaultValue: 1,
    }
    return (<form className={MealFormStyles.form}>
        <Input label={'Amount'} input={setInput} />
        <button>+ Add</button>
    </form>)
}
export default MealForm;
