import React, { useReducer } from "react"
import CartContext from "./CartContext"

const defaultCartState = {
    items: [],
    totalAmount: 0
};

const cartReducer = (state, action) => {
    if (action.type === 'add_item') {
        // concat method to create new array without editing existing array
        // const updatedItems = state.items.concat(action.target);
        // let updatedItems
        // const updatedAmount = state.totalAmount + (action.item.price * action.item.amount);

        // const dataExist = state.items.findIndex((item) => item.id === action.target.id)
        // console.log('dataExist')
        // console.log(dataExist)
        // const setCartData = state.items[dataExist]
        // console.log('setCartData')
