        // const existingCartItemIndex = state.items.findIndex((item) => item.id === action.item.id);
        // console.log(existingCartItemIndex)
        // const existingCartItem = state.items[existingCartItemIndex];
        // let updatedItems;

        // if (existingCartItem) {
        //     const updatedItem = {
        //         ...existingCartItem,
        //         amount: existingCartItem.amount + action.item.amount,
        //     };
        //     updatedItems = [...state.items];
        //     updatedItems[existingCartItemIndex] = updatedItem;
        // } else {
        //     updatedItems = state.items.concat(action.item);
        // }

        const updatedAmount = state.totalAmount + (action.target.price * action.item.amount);
        const existingCartItemIndex = state.items.findIndex((target) => target.id === action.target.id);
        console.log(existingCartItemIndex)
        const existingCartItem = state.items[existingCartItemIndex];
        let updatedItems;

        if (existingCartItem) {
            const updatedItem = {
                ...existingCartItem,
                amount: existingCartItem.amount + action.item.amount,
            };
            updatedItems = [...state.items];
            updatedItems[existingCartItemIndex] = updatedItem;
        } else {
            updatedItems = state.items.concat(action.item);
        }

        // if (dataExist) {
        //     console.log(setCartData)
        //     const updatedItem = {
        //         ...setCartData,
        //         amount: setCartData.amount + action.target.amount
        //     }
        //     updatedItems = [...state.items];
        //     updatedItems[dataExist] = updatedItem
        // } else {
        //     updatedItems = state.items.concat(action.target)
        // }

        return {
            items: updatedItems,
            totalAmount: updatedAmount
        };
    }
    return defaultCartState;
};
const AppProvider = (props) => {
    const [cartState, dispatchCartAction] = useReducer(cartReducer, defaultCartState);
    const addItemHandler = (item) => {
        dispatchCartAction({ type: 'add_item', target: item })
    };
    const removeItemHandler = (id) => {
        dispatchCartAction({ type: 'add_item', id: id })
    };
    const setContext = {
        items: cartState.items,
        totalAmount: cartState.totalAmount,
        addItem: addItemHandler,
        removeItem: removeItemHandler
    }
    return (<CartContext.Provider value={setContext}>{props.children}</CartContext.Provider>)
}

export default AppProvider
