import React, { useReducer } from "react"
import CartContext from "./CartContext"

const defaultCartState = {
    items: [],
    totalAmount: 0
};

const cartReducer = (state, action) => {
    if (action.type === 'add_item') {
        // concat method to create new array without editing existing array
        // const updatedItems = state.items.concat(action.target);
        let updatedItems
        const updatedAmount = state.totalAmount + (action.target.price * action.target.amount);

        const dataExist = state.items.findIndex((item) => item.id === action.target.id);
        console.log(dataExist)
        const setCartData = state.items[dataExist]
        console.log(setCartData)

        if (dataExist) {
            console.log(setCartData)
            const updatedItem = {
                ...setCartData,
                amount: setCartData.amount + action.target.amount
            }
            updatedItems = [...state.items];
            updatedItems[dataExist] = updatedItem
        } else {
            updatedItems = state.items.concat(action.target)
        }

        return {
            items: updatedItems,
            totalAmount: updatedAmount
        };
    }
    return defaultCartState;
};
const AppProvider = (props) => {
    const [cartState, dispatchCartAction] = useReducer(cartReducer, defaultCartState);
    const addItemHandler = (item) => {
        dispatchCartAction({ type: 'add_item', target: item })
    };
    const removeItemHandler = (id) => {
        dispatchCartAction({ type: 'add_item', id: id })
    };
    const setContext = {
        items: cartState.items,
        totalAmount: cartState.totalAmount,
        addItem: addItemHandler,
        removeItem: removeItemHandler
    }
    return (<CartContext.Provider value={setContext}>{props.children}</CartContext.Provider>)
}

export default AppProvider
